import React, { Component } from 'react';
import * as TodoService from '../service/TodoService';

export default class TodoForm extends Component {
  state = {
    addTodoContent: '',
    todos: [],
  };

  componentDidMount() {
    TodoService.allTodoService()
      .then((response) => {
        this.setState({ todos: response.data });
      })
      .catch((error) => {
        console.error('Todo listesi alınamadı:', error);
      });
  }

  handleInputChange = (event) => {
    this.setState({ addTodoContent: event.target.value });
  };

  handleAddTodo = (event) => {
    event.preventDefault();
  
    const { addTodoContent } = this.state;
  
    const todo = { content: addTodoContent }; // content değerini içeren bir nesne oluşturun
  
    TodoService.createTodoService(todo)
      .then((response) => {
        const addTodo = response.data;
        this.setState((prevState) => ({
          todos: [...prevState.todos, addTodo],
          addTodoContent: '',
        }));
      })
      .catch((error) => {
        console.error('Todo oluşturulamadı:', error);
      });
  };
  

  render() {
    const { addTodoContent, todos } = this.state;

    return (
      <div className="container mt-5">
        <h3 className="text-center">Todo Input</h3>
        <div className="card text-center rounded-0 mt-3">
          <div className="card-body">
            <form onSubmit={this.handleAddTodo}>
              <div className="input-group input-group-sm mb-3">
                <span className="input-group-text" id="inputGroup-sizing-sm">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="16"
                    height="16"
                    fill="currentColor"
                    className="bi bi-journal-plus"
                    viewBox="0 0 16 16"
                  >
                    <path fillRule="evenodd" d="M8 5.5a.5.5 0 0 1 .5.5v1.5H10a.5.5 0 0 1 0 1H8.5V10a.5.5 0 0 1-1 0V8.5H6a.5.5 0 0 1 0-1h1.5V6a.5.5 0 0 1 .5-.5z" />
                    <path d="M3 0h10a2 2 0 0 1 2 2v12a2 2 0 0 1-2 2H3a2 2 0 0 1-2-2v-1h1v1a1 1 0 0 0 1 1h10a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1H3a1 1 0 0 0-1 1v1H1V2a2 2 0 0 1 2-2z" />
                    <path d="M1 5v-.5a.5.5 0 0 1 1 0V5h.5a.5.5 0 0 1 0 1h-2a.5.5 0 0 1 0-1H1zm0 3v-.5a.5.5 0 0 1 1 0V8h.5a.5.5 0 0 1 0 1h-2a.5.5 0 0 1 0-1H1zm0 3v-.5a.5.5 0 0 1 1 0v.5h.5a.5.5 0 0 1 0 1h-2a.5.5 0 0 1 0-1H1z" />
                  </svg>
                </span>
                <input
                  type="text"
                  className="form-control"
                  aria-describedby="inputGroup-sizing-sm"
                  value={addTodoContent}
                  onChange={this.handleInputChange}
                />
              </div>
              <button type="submit" className="add-btn btn btn-primary mt-3">
                Add New Task
              </button>
            </form>
          </div>
        </div>
        <h3 className="text-center">Todo List</h3>
        <div className="card">
          <div className="card-body">
            <div className="row flex-wrap">
              <div className="col-md-4 d-flex justify-content-start card-buttons">
                <button className="btn btn-primary">All</button>
              </div>
              <div className="col-md-4 d-flex justify-content-center card-buttons">
                <button className="btn btn-primary">Done</button>
              </div>
              <div className="col-md-4 d-flex justify-content-end card-buttons">
                <button className="btn btn-primary">Todo</button>
              </div>
            </div>
            <div className="row mt-5">
              <div className="col">
                <ul className="list-group">
                  {todos.map((todo) => (
                    <li className="list-group-item d-flex justify-content-between" key={todo.id}>
                      {todo.content}
                      <div className="form-check">
                        <input className="form-check-input" type="checkbox" value="" id="flexCheckDefault" />
                        <a className="ms-2 updateItem" href="/">
                          <svg
                            xmlns="http://www.w3.org/2000/svg"
                            width="16"
                            height="16"
                            fill="currentColor"
                            className="bi bi-pencil-fill"
                            viewBox="0 0 16 16"
                          >
                            <path d="M12.854.146a.5.5 0 0 0-.707 0L10.5 1.793 14.207 5.5l1.647-1.646a.5.5 0 0 0 0-.708l-3-3zm.646 6.061L9.793 2.5 3.293 9H3.5a.5.5 0 0 1 .5.5v.5h.5a.5.5 0 0 1 .5.5v.5h.5a.5.5 0 0 1 .5.5v.207l6.5-6.5zm-7.468 7.468A.5.5 0 0 1 6 13.5V13h-.5a.5.5 0 0 1-.5-.5V12h-.5a.5.5 0 0 1-.5-.5V11h-.5a.5.5 0 0 1-.5-.5V10h-.5a.499.499 0 0 1-.175-.032l-.179.178a.5.5 0 0 0-.11.168l-2 5a.5.5 0 0 0 .65.65l5-2a.5.5 0 0 0 .168-.11l.178-.178z" />
                          </svg>
                        </a>
                        <a className="ms-2 deleteItem" href="/">
                          <svg
                            xmlns="http://www.w3.org/2000/svg"
                            width="16"
                            height="16"
                            fill="currentColor"
                            className="bi bi-trash"
                            viewBox="0 0 16 16"
                          >
                            <path d="M5.5 5.5A.5.5 0 0 1 6 6v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5Zm2.5 0a.5.5 0 0 1 .5.5v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5Zm3 .5a.5.5 0 0 0-1 0v6a.5.5 0 0 0 1 0V6Z" />
                            <path d="M14.5 3a1 1 0 0 1-1 1H13v9a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V4h-.5a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1H6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1h3.5a1 1 0 0 1 1 1v1ZM4.118 4 4 4.059V13a1 1 0 0 0 1 1h6a1 1 0 0 0 1-1V4.059L11.882 4H4.118ZM2.5 3h11V2h-11v1Z" />
                          </svg>
                        </a>
                      </div>
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
