import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

function ModalComponent({ todoId, handleCloseModal, handleUpdateTodo }) {
  const [updatedContent, setUpdatedContent] = useState('');

  const handleContentChange = (e) => {
    setUpdatedContent(e.target.value);
  };

  const handleSaveChanges = () => {
    handleUpdateTodo(todoId, updatedContent);
  };

  return (
    <Modal show={true} onHide={handleCloseModal}>
      <Modal.Header closeButton>
        <Modal.Title className='text-center w-100'>Todo GÃ¼ncelleme</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <form>
          <input
          className='form-control'
            type="text"
            value={updatedContent}
            onChange={handleContentChange}
          />
        </form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleSaveChanges}>
          Kaydet
        </Button>
      </Modal.Footer>
    </Modal>
  );
}

export default ModalComponent;
