import React, { Component } from 'react';
import * as TodoService from '../service/TodoService';
import ModalComponent from './Modal';
import TodoItem from './TodoItem';
import TodoForm from './TodoForm';

export default class TodoList extends Component {
    state = {
        todos: [],
        showModal: false,
        selectedTodoId: null,
        addTodoContent: '',
    };
    // MODAL
    handleOpenModal = (todoId) => {
        this.setState({
            showModal: true,
            selectedTodoId: todoId,
        });
    };
    handleCloseModal = () => {
        this.setState({
            showModal: false,
            selectedTodoId: null,
        });
    };
    // Todo Eklendiğinde liste güncelleme
    updateTodoList = (addTodo) => {
        this.setState((prevState) => ({
            todos: [...prevState.todos, addTodo],
            addTodoContent: '', // Formu sıfırla
        }));
    };
    // Todo List
    componentDidMount() {
        TodoService.allTodoService()
            .then((response) => {
                this.setState({ todos: response.data });
            })
            .catch((error) => {
                console.error('Todo listesi alınamadı:', error);
            });
    }
    // Delete Todo
    handleDeleteTodo = (event, todoId) => {
        event.preventDefault();
        TodoService.deleteTodoService(todoId)
            .then(() => {
                this.setState((prevState) => ({
                    todos: prevState.todos.filter((todo) => todo.id !== todoId),
                }));
            })
            .catch((error) => {
                console.error('Todo silinemedi:', error);
            });
    };
    // Add
    handleInputChange = (event) => {
        this.setState({ addTodoContent: event.target.value });
    };
    handleAddTodo = (event) => {
        event.preventDefault();
        const { addTodoContent } = this.state;
        const todo = { content: addTodoContent };
        TodoService.createTodoService(todo)
            .then((response) => {
                const addTodo = response.data;
                console.log('Todo oluşturuldu:', addTodo);
                this.updateTodoList(addTodo);
            })
            .catch((error) => {
                console.error('Todo oluşturulamadı:', error);
            });
    };
    handleCheck = (todoId) => {
        TodoService.checkTodoService(todoId)
            .then((response) => {
                // Başarılı güncelleme durumunda yapılacak işlemler
                console.log("Todo güncellendi:", response.data);
                // Todo güncellendikten sonra yerel durumu güncelleyin
                this.setState((prevState) => ({
                    todos: prevState.todos.map((todo) =>
                        todo.id === todoId ? { ...todo, checked: !todo.checked, isChecked: !todo.isChecked } : todo
                    ),
                }));
            })
            .catch((error) => {
                console.error("Todo güncellenemedi:", error);
            });
    };
    uncompletedTodoList() {
        TodoService.allUncompletedListService()
            .then((response) => {
                this.setState({ todos: response.data });
            })
            .catch((error) => {
                console.error('Todo listesi alınamadı:', error);
            });
    }
    doneTodoList() {
        TodoService.allDoneListService()
            .then((response) => {
                this.setState({ todos: response.data });
            })
            .catch((error) => {
                console.error('Todo listesi alınamadı:', error);
            });
    }
    doneDeleteTodo = (event) => {
        event.preventDefault();
        TodoService.deleteDoneAllService()
          .then(() => {
            // Tüm tamamlanan görevleri sildikten sonra yerel durumu güncelleyin
            this.setState(prevState => ({
              todos: prevState.todos.filter(todo => !todo.isChecked)
            }));
          })
          .catch((error) => {
            console.error('Todo silinemedi:', error);
          });
      };
      
    render() {
        const { todos, showModal, selectedTodoId, addTodoContent } = this.state;
        return (
            <React.Fragment>
                <TodoForm
                    handleAddTodo={this.handleAddTodo}
                    handleInputChange={this.handleInputChange}
                    addTodoContent={addTodoContent}
                />
                <h3 className="text-center">Todo List</h3>
                <div className="card">
                    <div className="card-body">
                        <div className="row flex-wrap">
                            <div className="col-md-4 d-flex justify-content-start card-buttons">
                                <button onClick={() => this.componentDidMount()} className="btn btn-primary">All</button>
                            </div>
                            <div className="col-md-4 d-flex justify-content-center card-buttons">
                                <button onClick={() => this.doneTodoList()} className="btn btn-primary">Done</button>
                            </div>
                            <div className="col-md-4 d-flex justify-content-end card-buttons">
                                <button onClick={() => this.uncompletedTodoList()} className="btn btn-primary">Todo</button>
                            </div>
                        </div>
                        <div className="row mt-5">
                            <div className="col">
                                <ul className="list-group">
                                    {todos.map((todo) => (
                                        <TodoItem
                                            todo={todo}
                                            handleOpenModal={this.handleOpenModal}
                                            handleDeleteTodo={this.handleDeleteTodo}
                                            handleCheck={this.handleCheck}
                                            checked={todo.isChecked}
                                        />
                                    ))}
                                </ul>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="row flex-wrap delete-buttons">
                            <div className="col-md-6 d-flex justify-content-start card-buttons">
                                <button onClick={(event) => handleDeleteTodo(event)} className="btn btn-primary">Delete Done Tasks</button>
                            </div>
                            <div className="col-md-6 d-flex justify-content-end card-buttons">
                                <button onClick={() => this.uncompletedTodoList()} className="btn btn-primary">Delete All Tasks</button>
                            </div>
                        </div>
                {showModal && (
                    <ModalComponent
                        todoId={selectedTodoId}
                        handleCloseModal={this.handleCloseModal}
                    />
                )}
            </React.Fragment>
        );
    }
}
