import React, { Component } from 'react';
import * as TodoService from '../service/TodoService';
import ModalComponent from './Modal';
import TodoItem from './TodoItem';
import TodoForms from './TodoForms';

export default class TodoList extends Component {
    state = {
        todos: [],
        showModal: false,
        selectedTodoId: null,
        addTodoContent:'',
    };
    //MODAL
    handleOpenModal = (todoId) => {
        this.setState({
            showModal: true,
            selectedTodoId: todoId,
        });
    };
    handleCloseModal = () => {
        this.setState({
            showModal: false,
            selectedTodoId: null,
        });
    };
    //Todo Eklendiğinde liste güncelleme
    componentDidUpdate(prevProps) {
        if (this.props.todos !== prevProps.todos) {
            this.componentDidMount();
        }
    }
    //Todo List
    componentDidMount() {
        TodoService.allTodoService()
            .then((response) => {
                this.setState({ todos: response.data });
            })
            .catch((error) => {
                console.error('Todo listesi alınamadı:', error);
            });
    }
    //Delete Todo
    handleDeleteTodo = (event, todoId) => {
        event.preventDefault();
        TodoService.deleteTodoService(todoId)
            .then(() => {
                this.setState((prevState) => ({
                    todos: prevState.todos.filter((todo) => todo.id !== todoId),
                }));
            })
            .catch((error) => {
                console.error('Todo silinemedi:', error);
            });
    };
       //Add
       handleInputChange = (event) => {
        this.setState({ addTodoContent: event.target.value });
    };
    handleAddTodo = (event) => {
        event.preventDefault();
        const { addTodoContent } = this.state;
        const todo = { content: addTodoContent }; // content değerini içeren bir nesne oluşturun
        TodoService.createTodoService(todo)
            .then((response) => {
                const addTodo = response.data;
                console.log('Todo oluşturuldu:', addTodo);
                this.props.onTodoAdded(addTodo);
            })
            .catch((error) => {
                console.error('Todo oluşturulamadı:', error);
            });
    };
    render() {
        const { todos, showModal, selectedTodoId } = this.state;
        return (
            <React.Fragment>
                <TodoForms onTodoAdded={this.updateTodoList}>

                </TodoForms>
                <h3 className="text-center">Todo List</h3>
                <div className="card">
                    <div className="card-body">
                        <div className="row flex-wrap">
                            <div className="col-md-4 d-flex justify-content-start card-buttons">
                                <button className="btn btn-primary">All</button>
                            </div>
                            <div className="col-md-4 d-flex justify-content-center card-buttons">
                                <button className="btn btn-primary">Done</button>
                            </div>
                            <div className="col-md-4 d-flex justify-content-end card-buttons">
                                <button className="btn btn-primary">Todo</button>
                            </div>
                        </div>
                        <div className="row mt-5">
                            <div className="col">
                                <ul className="list-group">{todos.map((todo) => (
                                        <TodoItem
                                            todo={todo}
                                            handleOpenModal={this.handleOpenModal}
                                            handleDeleteTodo={this.handleDeleteTodo}
                                        />
                                    ))}
                                </ul>
                            </div>
                        </div>
                    </div>
                </div>
                {showModal && (
                    <ModalComponent
                        todoId={selectedTodoId}
                        handleCloseModal={this.handleCloseModal}
                    />
                )}
            </React.Fragment>
        );
    }
}
