import React, { useState, useEffect, } from 'react';
import * as TodoService from '../service/TodoService';
import ModalComponent from './Modal';
import TodoItem from './TodoItem';
import TodoForm from './TodoForm';

const TodoList = () => {
    const [todos, setTodos] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [selectedTodoId, setSelectedTodoId] = useState(null);
    const [addTodoContent, setAddTodoContent] = useState('');

    useEffect(() => {
        fetchAllTodos();
    }, []);

    const fetchAllTodos = () => {
        TodoService.allTodoService()
            .then((response) => {
                setTodos(response.data);
            })
            .catch((error) => {
                console.error('Todo listesi alınamadı:', error);
            });
    };

    const handleOpenModal = (todoId) => {
        setShowModal(true);
        setSelectedTodoId(todoId);
        setAddTodoContent('');
    };

    const handleCloseModal = () => {
        setShowModal(false);
        setSelectedTodoId(null);
    };

    const updateTodoList = (addTodo) => {
        setTodos((prevTodos) => [...prevTodos, addTodo]);
        setAddTodoContent('');
    };

    const handleDeleteTodo = (event, todoId) => {
        event.preventDefault();
        TodoService.deleteTodoService(todoId)
            .then(() => {
                setTodos((prevTodos) => prevTodos.filter((todo) => todo.id !== todoId));
            })
            .catch((error) => {
                console.error('Todo silinemedi:', error);
            });
    };

    const handleInputChange = (event) => {
        setAddTodoContent(event.target.value);
    };

    const handleAddTodo = (event) => {
        event.preventDefault();
        const todo = { content: addTodoContent };
        TodoService.createTodoService(todo)
            .then((response) => {
                const addTodo = response.data;
                console.log('Todo oluşturuldu:', addTodo);
                updateTodoList(addTodo);
            })
            .catch((error) => {
                console.error('Todo oluşturulamadı:', error);
            });
    };

    const handleCheck = (todoId) => {
        TodoService.checkTodoService(todoId)
            .then((response) => {
                console.log('Todo güncellendi:', response.data);
                setTodos((prevTodos) =>
                    prevTodos.map((todo) =>
                        todo.id === todoId ? { ...todo, checked: !todo.checked, isChecked: !todo.isChecked } : todo
                    )
                );
            })
            .catch((error) => {
                console.error('Todo güncellenemedi:', error);
            });
    };

    const uncompletedTodoList = () => {
        TodoService.allUncompletedListService()
            .then((response) => {
                setTodos(response.data);
            })
            .catch((error) => {
                console.error('Todo listesi alınamadı:', error);
            });
    };

    const doneTodoList = () => {
        TodoService.allDoneListService()
            .then((response) => {
                setTodos(response.data);
            })
            .catch((error) => {
                console.error('Todo listesi alınamadı:', error);
            });
    };

    const doneDeleteTodo = (event) => {
        event.preventDefault();
        TodoService.deleteDoneAllService()
            .then(() => {
                setTodos((prevTodos) => prevTodos.filter((todo) => !todo.isChecked));
            })
            .catch((error) => {
                console.error('Todo silinemedi:', error);
            });
    };

    const allDeleteTodo = (event) => {
        event.preventDefault();
        TodoService.allDeleteService()
            .then(() => {
                setTodos([]);
            })
            .catch((error) => {
                console.error('Todo silinemedi:', error);
            });
    };

    const handleUpdateTodo = (id, updatedContent) => {
        const updatedTodo = { id, content: updatedContent };
        TodoService.updateTodoService(id, JSON.stringify(updatedTodo), 'application/json')
            .then((response) => {
                console.log('Todo güncellendi:', response.data);
                setTodos((prevTodos) =>
                    prevTodos.map((todo) =>
                        todo.id === id ? { ...todo, content: updatedContent } : todo
                    )
                );
                handleCloseModal();
            })
            .catch((error) => {
                console.error('Todo güncellenemedi:', error);
            });
    };


      
    return (
        <>
            <TodoForm
                handleAddTodo={handleAddTodo}
                handleInputChange={handleInputChange}
                addTodoContent={addTodoContent}
            />
            <h3 className="text-center">Todo List</h3>
            <div className="card">
                <div className="card-body ">
                    <div className="row flex-wrap">
                        <div className="col-md-4 d-flex justify-content-start ">
                            <button onClick={fetchAllTodos} className="btn btn-primary">All</button>
                        </div>
                        <div className="col-md-4 d-flex justify-content-center ">
                            <button onClick={doneTodoList} className="btn btn-primary">Done</button>
                        </div>
                        <div className="col-md-4 d-flex justify-content-end ">
                            <button onClick={uncompletedTodoList} className="btn btn-primary">Todo</button>
                        </div>
                    </div>
                    <div className="row mt-5">
                        <div className="col">
                            <ul className="list-group todo-list-body" >
                                {todos.map((todo) => (
                                    <TodoItemus
                                        key={todo.id}
                                        todo={todo}
                                        handleOpenModal={handleOpenModal}
                                        handleDeleteTodo={handleDeleteTodo}
                                        handleCheck={handleCheck}
                                        checked={todo.isChecked}
                                    />

                                ))};
                                
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
            <div className="row flex-wrap delete-buttons">
                <div className="col-md-6 d-flex justify-content-start card-buttons">
                    <button onClick={doneDeleteTodo} className="btn btn-primary">Delete Done Tasks</button>
                </div>
                <div className="col-md-6 d-flex justify-content-end card-buttons">
                    <button onClick={allDeleteTodo} className="btn btn-primary">Delete All Tasks</button>
                </div>
            </div>
            {showModal && (
                <ModalComponent
                    todoId={selectedTodoId}
                    handleCloseModal={handleCloseModal}
                    handleUpdateTodo={handleUpdateTodo}
                />
            )}
        </>
    );
};

export default TodoList;
